<?php

if (!defined('BASEPATH'))
    exit('No direct script access allowed');

class Ass_produk_model extends CI_Model
{

    public $table = 'ass_produk';
    public $id = 'produk_id';
    public $order = 'DESC';

    function __construct()
    {
        parent::__construct();
    }

    // get all
    function get_all()
    {
        $this->db->order_by($this->id, $this->order);
        return $this->db->get($this->table)->result();
    }

    // get data by id
    function get_by_id($id)
    {
        $this->db->where($this->id, $id);
        return $this->db->get($this->table)->row();
    }
    
    // get total rows
    function total_rows($q = NULL) {
        $this->db->like('produk_kategori_id', $q);
        $this->db->or_like('produk_nama', $q);
        $this->db->or_like('produk_tglinput', $q);
        $this->db->or_like('produk_status', $q);
        $this->db->from($this->table);
        return $this->db->count_all_results();
    }

    // get data with limit and search
    function get_limit_data($limit, $start = 0, $q = NULL) {
        $this->db->join('jp_job_kategori','produk_kategori_id=kategori_id');
        $this->db->order_by($this->id, $this->order);
        $this->db->like('produk_kategori_id', $q);
        $this->db->or_like('produk_nama', $q);
        $this->db->or_like('produk_tglinput', $q);
        $this->db->or_like('produk_status', $q);
        $this->db->limit($limit, $start);
        return $this->db->get($this->table)->result();
    }

    // insert data
    function insert($data)
    {
        $this->db->insert($this->table, $data);
    }

    // update data
    function update($id, $data)
    {
        $this->db->where($this->id, $id);
        $this->db->update($this->table, $data);
    }

    // delete data
    function delete($id)
    {
        $this->db->where($this->id, $id);
        $this->db->delete($this->table);
    }
    function get_tst_kategori()
    {
        $this->db->where('kategori_status','Aktif');
        return $this->db->get('jp_job_kategori')->result();
    }

    public function ambil_unit_from($produk_id){
        $this->db->join('jp_job_produk_relasi','relasi_unit_id=unit_id','left');
        $this->db->join('jp_job_produk','relasi_produk_id=produk_id','left');
        $this->db->group_start();
        $this->db->where('produk_id IS NULL');
        $this->db->or_where('produk_id <>',$produk_id);
        $this->db->group_end();
        $this->db->order_by('unit_id','asc');
        $sql_unit=$this->db->get('jp_job_unit');
        if($sql_unit->num_rows()>0){

            foreach ($sql_unit->result_array() as $row)
            {
                $result[$row['unit_id']]= ucwords(strtolower($row['unit_kode']) ."-" .strtolower($row['unit_judul']));
            }
        } else {
            $result['-']= '- Belum Ada Unit -';
        }
        return $result;
    }


    public function ambil_unit_to($produk_id){
        $this->db->join('jp_job_produk_relasi','relasi_unit_id=unit_id','left');
        $this->db->join('jp_job_produk','relasi_produk_id=produk_id','left');
        $this->db->group_start();
        $this->db->or_where('produk_id',$produk_id);
        $this->db->group_end();
        $this->db->order_by('unit_id','asc');
        $sql_unit=$this->db->get('jp_job_unit');
        if($sql_unit->num_rows()>0){

            foreach ($sql_unit->result_array() as $row)
            {
                $result[$row['unit_id']]= ucwords(strtolower($row['unit_kode']) ."-" .strtolower($row['unit_judul']));
            }
        } else {
            $result= array();
        }
        return $result;
        
    }

    public function get_produk_byid($id){
        $this->db->where('produk_id',$id);
        $this->db->order_by('produk_id', $this->order);
        return $this->db->get('jp_job_produk')->row();
        //return $query->row();
    }
}

