class Coba_model extends CI_Model
{

    public $table = 'coba';
    public $id = 'coba_id';
    public $order = 'DESC';

    function __construct()
    {
        parent::__construct();
    }

    // get all
    function get_all()
    {
        $this->db->order_by($this->id, $this->order);
        return $this->db->get($this->table)->result();
    }

    // get data by id
    function get_by_id($id)
    {
        $this->db->where($this->id, $id);
        return $this->db->get($this->table)->row();
    }
    
    // get total rows
    function total_rows($q = NULL) {
        $this->db->like('coba_id', $q);
	$this->db->or_like('coba_nama', $q);
	$this->db->or_like('coba_desc', $q);
	$this->db->or_like('coba_tgl', $q);
	$this->db->or_like('coba_status1', $q);
	$this->db->or_like('coba_status2', $q);
	$this->db->or_like('coba_status3', $q);
	$this->db->or_like('coba_status4', $q);
	$this->db->or_like('coba_status5', $q);
	$this->db->or_like('coba_jekel', $q);
	$this->db->from($this->table);
        return $this->db->count_all_results();
    }

    // get data with limit and search
    function get_limit_data($limit, $start = 0, $q = NULL) {
        $this->db->order_by($this->id, $this->order);
        $this->db->like('coba_id', $q);
	$this->db->or_like('coba_nama', $q);
	$this->db->or_like('coba_desc', $q);
	$this->db->or_like('coba_tgl', $q);
	$this->db->or_like('coba_status1', $q);
	$this->db->or_like('coba_status2', $q);
	$this->db->or_like('coba_status3', $q);
	$this->db->or_like('coba_status4', $q);
	$this->db->or_like('coba_status5', $q);
	$this->db->or_like('coba_jekel', $q);
	$this->db->limit($limit, $start);
        return $this->db->get($this->table)->result();
    }

    // insert data
    function insert($data)
    {
        $this->db->insert($this->table, $data);
    }

    // update data
    function update($id, $data)
    {
        $this->db->where($this->id, $id);
        $this->db->update($this->table, $data);
    }

    // delete data
    function delete($id)
    {
        $this->db->where($this->id, $id);
        $this->db->delete($this->table);
    }

}

