<?php

if (!defined('BASEPATH'))
    exit('No direct script access allowed');

class Expert_model extends CI_Model
{

    public $table = 'tbl_user';
    public $id = 'username';
    public $order = 'DESC';

    function __construct()
    {
        parent::__construct();
    }

    // get all
    function get_all()
    {
        $this->db->order_by($this->id, $this->order);
        return $this->db->get($this->table)->result();
    }

    // get data by id
    function get_by_id($id)
    {
        $this->db->where($this->id, $id);
        return $this->db->get($this->table)->row();
    }

    // get data by id
    function get_expert_group()
    {
        $this->db->where('group_name','IT Expert');
        return $this->db->get('tbl_group')->row();
    }
    
    // get total rows
    function total_rows($q = NULL) {
        $this->db->like('username', $q);
        $this->db->or_like('user_nama_lengkap', $q);
        $this->db->or_like('user_jekel', $q);
        $this->db->or_like('user_alamat', $q);
        $this->db->or_like('user_email', $q);
        $this->db->or_like('user_kontak', $q);
        $this->db->or_like('user_status', $q);
        $this->db->from($this->table);
        return $this->db->count_all_results();
    }

    // get data with limit and search
    function get_limit_data($limit, $start = 0, $q = NULL) {
        $this->db->join('tbl_group','tbl_user.id_group=tbl_group.id_group');
        $this->db->order_by($this->id, $this->order);
        $this->db->group_start();
        $this->db->like('username', $q);
        $this->db->or_like('user_nama_lengkap', $q);
        $this->db->or_like('user_jekel', $q);
        $this->db->or_like('user_alamat', $q);
        $this->db->or_like('user_email', $q);
        $this->db->or_like('user_kontak', $q);
        $this->db->or_like('user_status', $q);
        $this->db->group_end();
        $this->db->where('group_name','IT Expert');
        $this->db->limit($limit, $start);
        return $this->db->get($this->table)->result();
    }

    // insert data
    function insert($data)
    {
        $this->db->insert($this->table, $data);
    }

    // update data
    function update($id, $data)
    {
        $this->db->where($this->id, $id);
        $this->db->update($this->table, $data);
    }

    // delete data
    function delete($id)
    {
        $this->db->where($this->id, $id);
        $this->db->delete($this->table);
    }
}

