	$this->db->or_like('pengalaman_perusahaan', $q);
	$this->db->or_like('pengalaman_bidang', $q);
	$this->db->from($this->table);
        return $this->db->count_all_results();
    }

    // get data with limit and search
    function get_limit_data($limit, $start = 0, $q = NULL) {
        $this->db->order_by($this->id, $this->order);
        $this->db->like('pengalaman_id', $q);
	$this->db->or_like('pengalaman_member_id', $q);
	$this->db->or_like('pengalaman_dari', $q);
