<?php

if (!defined('BASEPATH'))
    exit('No direct script access allowed');

class Promo_model extends CI_Model
{

    public $table = 'jp_promo';
    public $id = 'promo_id';
    public $order = 'DESC';

    function __construct()
    {
        parent::__construct();
    }

    // get all
    function get_all()
    {
        $this->db->order_by($this->id, $this->order);
        return $this->db->get($this->table)->result();
    }

    // get all
    function list_member_promo($promo_id,$produk_id)
    {
        $this->db->join('jp_member','member_id=enrol_member_id');
        $this->db->join('jp_promo','promo_id=enrol_promo_id');
        $this->db->join('jp_job_produk','produk_id=member_produk_id');
        $this->db->where('promo_id',$promo_id);
        $this->db->where('produk_id',$produk_id);
        $this->db->order_by('enrol_id', $this->order);
        return $this->db->get('jp_promo_enroll')->result();
    }

    // get data by id
    function get_by_id($id)
    {
        $this->db->where($this->id, $id);
        return $this->db->get($this->table)->row();
    }

    // get data by id
    function get_by_status($status)
    {
        $sekarang=date('Y-m-d');
        $this->db->where('promo_mulai <= ', $sekarang);
        $this->db->where('promo_sampai >= ', $sekarang);
        $this->db->where('promo_status', $status);
        return $this->db->get($this->table)->result();
    }

    public function get_promo_byid($id){
        $this->db->where('promo_id',$id);
        return $this->db->get('jp_promo')->row();
        //return $query->row();
    }

    public function count_talent_produk_by_id($id){
        $sekarang=date('Y-m-d');
        $this->db->select("count(enrol_member_id) as jml_member, member_produk_id, produk_nama, enrol_promo_id");
        $this->db->join('jp_member','member_id=jp_promo_enroll.enrol_member_id');
        $this->db->join('jp_job_produk','produk_id=jp_member.member_produk_id');
        $this->db->join('jp_promo','enrol_promo_id=promo_id');
        $this->db->where('promo_mulai <= ', $sekarang);
        $this->db->where('promo_sampai >= ', $sekarang);
        $this->db->where('enrol_promo_id',$id);
        $this->db->group_by('member_produk_id');
        return $this->db->get('jp_promo_enroll')->result();
    }

    // get total rows
    function total_rows($q = NULL) {
        $this->db->like('promo_nama', $q);
        $this->db->or_like('promo_mulai', $q);
        $this->db->or_like('promo_sampai', $q);
        $this->db->or_like('promo_status', $q);
        $this->db->from($this->table);
        return $this->db->count_all_results();
    }

    // get data with limit and search
    function get_limit_data($limit, $start = 0, $q = NULL) {
        $this->db->order_by($this->id, $this->order);
        $this->db->like('promo_nama', $q);
        $this->db->or_like('promo_mulai', $q);
        $this->db->or_like('promo_sampai', $q);
        $this->db->or_like('promo_status', $q);
        $this->db->limit($limit, $start);
        return $this->db->get($this->table)->result();
    }

    public function list_member($source_id,$produk_id){
        $this->db->join('jp_job_produk','member_produk_id=produk_id','left');
        $this->db->join('jp_job_tipe','member_tipe_id=tipe_id','left');
        $this->db->join('jp_promo_enroll','member_id=enrol_member_id','left');
        $this->db->where('enrol_id IS NULL');
        $this->db->where('member_sumber_id',$source_id);
        $this->db->where('member_produk_id',$produk_id);
        $this->db->order_by('member_id','desc');
        return $this->db->get('jp_member')->result();
        
    }

    public function list_enrolled($promo_id){
        $this->db->join('jp_job_produk','member_produk_id=produk_id','left');
        $this->db->join('jp_job_tipe','member_tipe_id=tipe_id','left');
        $this->db->join('jp_promo_enroll','member_id=enrol_member_id','left');
        $this->db->where('enrol_promo_id',$promo_id);
        $this->db->order_by('member_id','desc');
        return $this->db->get('jp_member')->result();
        
    }

    // insert data
    function insert($data)
    {
        $this->db->insert($this->table, $data);
    }

    // update data
    function update($id, $data)
    {
        $this->db->where($this->id, $id);
        $this->db->update($this->table, $data);
    }

    // delete data
    function delete($id)
    {
        $this->db->where($this->id, $id);
        $this->db->delete($this->table);
    }
}

